import React from 'react';
import { Link } from 'react-router-dom';

const Task = props => {
  return <div className="task-wrapper" id={props.task.id} >
    <h2>Nombre de tarea:</h2>
    <p>{props.task.name}</p>
    <Link to={`/detail`} className="card-btn">
      Ver detalle
    </Link>
  </div>
}

export default Task;
